import { useState } from 'react';
import { w3cwebsocket as W3CWebSocket } from 'websocket';

const client = new W3CWebSocket('ws://localhost:5000');

function Blog() {

    const [blog, setBlog] = useState({
        title:"", content:"", user_id:""
    });

    let name, value;
    const handleInput = (e) => {
        name =  e.terget.name;
        value = e.target.value;

        setBlog({...blog, [name]:value});
    }
    
    const onButtonClicked = ()=> {
        //send messsage from client to server
        client.send(JSON.stringify(blog));
    };

    //client connection confirmation
    client.onopen = () => {
        console.log('WebSocket Client Connected.');
    };

    //got message from server
    client.onmessage = (message) => {
        const dataFromServer = JSON.parse(message.data);
        console.log('Got Reply! ', dataFromServer);
    };

    return (<>
        <input type="text" name="title" value={blog.title} onChange={handleInput}/>
        <textarea name="content" onChange={handleInput}>{blog.content}
        </textarea>
        <input type="hidden" name="user_id" value={blog.user_id}/>

        <button onClick={onButtonClicked}>Post</button>
    </>);
}

export default Blog;
