import { useState } from "react";
import { useHistory } from "react-router-dom";
function Signin(){

    const history = useHistory();
    const [user, setuser] = useState({
        name:"", email:"", password:"", cpassword:""
    });


    const handleInputs = (e) => {
        let name  = e.target.name;
        let value = e.target.value;

        setUser({...user, [name]:value});

    }

    const PostData = async (e) => {
        e.preventDefault();

        const {name, email, password, cpassword} = user;

        const res = await fetch('/signin', {
            method: "POST",
            headers: {
                'Content-Type': "application/json"
            },
            body: JSON.stringify({
                name, email, password, cpassword
            })
        });

        const data = await res.json();

        if(data.status == 422 || !data){
            window.alert("Invalid Details");
        } else {
            window.alert("Registration successfully");
            history.push('/signup');
        }
    }
    return (<>
     <form methos="POST">
        <input type="name" placeholder='Enter your Full Name' value={user.name} onChange={handleInput}/>
        <input type="email" placeholder='Enter your email' value={user.email} onChange={handleInput}/>
        <input type="password" placeholder='Enter your password' value={user.password} onChange={handleInput}/>
        <input type="cpassword" placeholder='Re-enter your password' value={user.cpassword} onChange={handleInput}/>
        <button onClick={PostData}>Login</button>
    </form>
    </>)
}

export default Signin;
